import { React, useState} from 'react';

// Form Input Component
function FormInput({ id, name, type = "text", label, required = false }) {
  return (
    <div className="mb-3">
      <label htmlFor={id} className="form-label">{label}</label>
      <input type={type} className="form-control" id={id} name={name} required={required} />
    </div>
  );
}

// Form Textarea Component
function FormTextarea({ id, name, label }) {
  return (
    <div className="mb-3">
      <label htmlFor={id} className="form-label">{label}</label>
      <textarea className="form-control" id={id} name={name}></textarea>
    </div>
  );
}

// Form Checkbox Component
function FormCheckbox({ id, name, label, required = false }) {
  return (
    <div className="mb-3 form-check">
      <input type="checkbox" className="form-check-input" id={id} name={name} required={required} />
      <label className="form-check-label" htmlFor={id}>{label}</label>
    </div>
  );
}

// Footer Component
function Footer() {
  return (
    <footer className="bg-light py-3 mt-5">
      <div className="container text-center">
        <p>&copy; 2023 Journemo. All rights reserved.</p>
        <p id="contact">
          Contact Us: <a href="mailto:info@journemo.com"><span className="material-icons"></span>info@journemo.com</a>
        </p>
      </div>
    </footer>
  );
}

// Main AddEventForm Component
function AddEventForm(props) {
  const [dayNumber, setDayNumber] = useState('');
  const [eventName, setEventName] = useState('');
  const [location, setLocation] = useState('');
  const [description, setDescription] = useState('');

  const handleDayNumberChange = (e) => {
    setDayNumber(e.target.value);
    console.log(dayNumber);
  };
  const handleEventNameChange = (e) => {
      setEventName(e.target.value);
  };
  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };
  
  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  /*
  const handleSubmit = (e) => {
    e.preventDefault();
    props.addEvents({ dayNumber,eventName,location,description });
  };
  */

  return (
    <div>
      <main className="container mt-4 bg-sky">
        <h1>Add a New Event!</h1>
        <form>
          <div className="mb-3">
            <label htmlFor="dayNumber" className="form-label">Event Number:</label>
            <input type="number" className="form-control" id="dayNumber" name="dayNumber" onChange={handleDayNumberChange} required></input>
          </div>

          <div className="mb-3">
            <label htmlFor="eventName" className="form-label">Event Name:</label>
            <textarea className="form-control" id="eventName" name="eventName" onChange={handleEventNameChange} required></textarea>
          </div>
        
          <div className="mb-3">
            <label htmlFor="location" className="form-label">Location:</label>
            <textarea className="form-control" id="location" name="location" onChange={handleLocationChange} required></textarea>
          </div>
          
          
          <div className="mb-3">
            <label htmlFor="description" className="form-label">Description:</label>
            <textarea className="form-control" id="description" name="description" onChange={handleDescriptionChange} required></textarea>
          </div>
          
          <div className="mb-3 form-check">
            <input type="checkbox" className="form-check-input" id="itinerary" name="itinerary" required />
            <label className="form-check-label" htmlFor="itinerary">Is It An Itinerary?</label>
          </div>
          <button type="submit" className="btn btn-primary">Add Event</button>
        </form>
      </main>
      <Footer />
    </div>
  );
}

export default AddEventForm;
